import React from 'react';
function Para(){

    const t =new Date().getHours()
let greet=''
let cssStyle={}

if(t>=1 && t<12){
    greet='good morning'
    cssStyle.color='red'
}else if (t>=2 && t<19){
    greet='good afternoon'
    cssStyle.color='blue'
}else{
    // eslint-disable-next-line no-unused-vars
    greet='nigth'
    cssStyle.color='green'
}
    

return(
    <>
<h1>hello<span style={cssStyle}>{greet}</span></h1>
    </>
)
}

export default Para
